function ShowLoginErrorMessage(div, messageType, message){
    $(div).notify(message,{
      className: messageType,
      autoHide: true,
      clickToHide:true,
      autoHideDelay:45000,
     });
  }

function DataTable(table, title, columnArray){
  table.dataTable({
    paging:false,
    select:false,
    "bLengthChange": false,
    oLanguage: {
     sLengthMenu: "Show _MENU_ entries",
   },
   dom: '<"top"lf><B>rt<"bottom"<ip>>',
   buttons: [

extend( true, {}, {
    extend: 'excelHtml5',
    text:'<i class="fa fa-download"></i> Download Excel',
    title:title,
     exportOptions: {
      columns: columnArray
    },

  } ),

  $.extend( true, {}, {
    extend: 'pdfHtml5',
    text:'<i class="fa fa-download text-info"></i> Pdf',
    title:title,
     exportOptions: {
      columns: columnArray
    },

  } ),

  $.extend( true, {}, {
    extend: 'print',
    exportOptions:{
      columns: columnArray,
      modifier:{
        selected:null
      }
    },
    text:'<i class="fa fa-save"></i> Print',
    title:title,

  } ),

  $.extend( true, {}, {
    extend: 'print',
    text:'<i class="fa fa-save text-success"></i> Print Selected',
    title:title,
     exportOptions: {
      columns: columnArray
    },

  } ),
  ]
});
}


